import React, { useEffect, useState } from 'react';
import './Main.css';

import api from '../services/api';

import logo from '../assets/logo.svg'
import like from '../assets/like.svg'
import dislike from '../assets/dislike.svg'

export default function Main({ match }) {

    const [users, setUsers] = useState([]);

    useEffect(() => {
        async function loadUsers() {
            const response = await api.get('/devs', {
                headers: {
                    user: match.params.id,
                }
            })
            console.log(response.data);
            setUsers(response.data);
        }

        loadUsers();
    }, [match.params.id]);

    async function handleLike(id) {
        console.log('handleLike', id);
        await api.post(`/devs/${id}/likes`, null, { headers: { user: match.params.id }, })

    }

    async function handleDislike(id) {
        console.log('handleDislike', id);
        await api.post(`/devs/${id}/dislikes`, null, { headers: { user: match.params.id }, })

        setUsers(users.filter(user => user._id !== id));
    }

    return (
        <div className='main-container'>
            <img src={logo} alt='TinDev' />
            <ul>
                {users.map(user => (
                    <li key={user._id} >
                        <img src={user.avatar} alt={user.name} />
                        <footer>
                            <strong>{user.name}</strong>
                            <p>{user.bio}</p>
                        </footer>
                        <div className='buttons'>
                            <button type='button'>
                                <img src={dislike} alt='Dislike' onClick={() => handleDislike(user._id)} />
                            </button>
                            <button type='button'>
                                <img src={like} alt='Like' onClick={() => handleLike(user._id)} />
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    )
}
